package com.juliandominguez.dam_task_02

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.juliandominguez.dam_task_02.`class`.Hero
import com.juliandominguez.dam_task_02.adapters.HeroAdapter
import com.juliandominguez.dam_task_02.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {
    private lateinit var _heroAdapter:HeroAdapter
    private lateinit var _linealLayoutManager:RecyclerView.LayoutManager
    private lateinit var _viewBinding: ActivityMainBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        _viewBinding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(_viewBinding.root)

        _heroAdapter= HeroAdapter(_getHeroes())
        _linealLayoutManager = LinearLayoutManager(this)

        _viewBinding.rvListHero.apply {
            layoutManager = _linealLayoutManager;
            adapter = _heroAdapter
        }
    }

    private fun _getHeroes(): MutableList<Hero>{

        val heroes = mutableListOf<Hero>()

        val ironMan = Hero("Iron Man","Tony Star","data:image/jpeg;base64,")
        val blackWindow = Hero("Black Window","Natasha Romanoff","https://de10.com.mx/sites/default/files/styles/detalle_nota/public/2019/06/03/black_widow_pelicula_filtradas_p.jpg?itok=5Uy24wbw")
        val scarletWitch = Hero("Scarlet Witch", "Wanda Maximoff","https://media.apnarm.net.au/media/images/2019/05/20/imagev109f92e4dece38eccd8e5a974565da859-1pf8tmznomqtyqgvbs2_t1880.jpg")

        for (i in 1..6){
            heroes.add(ironMan)
            heroes.add(blackWindow)
            heroes.add(scarletWitch)
        }

        return heroes

    }
}